INSERT INTO `produks` (`id`, `nama`, `deskripsi`, `small_image`, `large_image`, `icon_pakaian`, `created_at`, `updated_at`, `icon`) VALUES
(1, 'Attack Violet Aroma<br/>800 gr', 'Detergent konsentrat dengan formula  dashyat 3D Clean Action sehingga pakaian Anda  bersih, lembut, mudah disetrika, wangi segar tahan lama karena perpaduan wangi bunga mawar, melati dan bakung.<br><br>Pakaian bersih menyeluruh karena Ultra Biolite mudah menghilangkan noda membandel hingga ke serat kain. <br>Pakaian menjadi lembut dan nyaman karena pelembut alami UltraSoft membuat serat kain menjadi lentur.<br>Mudah disetrika kain tidak kusut setelah proses pengeringan atau penjemuran.<br><i>* Takaran mencuci 1 sendok untuk 15 potong pakaian </i><br>', '/img/produk/small/produk-01.png', '/img/produk/medium/produk-01.png', '/img/produk/icon/handmachinewash.png', '2015-03-21 17:00:00', '0000-00-00 00:00:00', ''),
(2, 'Attack Plus Softener<br/>23 gr, 48 gr, 450 gr, 800 gr, 1200 gr', 'Detergent konsentrat dengan formula  dashyat 3D Clean Action sehingga pakaian Anda bersih, lembut dan mudah disetrika, kelembutan alaminya membuat pakaian anda menjadi  lentur dan lembut.<br><br>Pakaian bersih menyeluruh karena Ultra Biolite mudah menghilangkan noda membandel hingga ke serat kain. <br>Pakaian menjadi lembut dan nyaman karena pelembut alami UltraSoft membuat serat kain menjadi lentur.<br>Mudah disetrika kain tidak kusut setelah proses pengeringan atau penjemuran.<br><i>* Takaran mencuci 1 sendok untuk 15 potong pakaian </i><br>', '/img/produk/small/produk-02.png', '/img/produk/medium/produk-02.png', '/img/produk/icon/handmachinewash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(3, 'Attack Clean Maximizer<br/>48 gr, 800 gr', 'Detergent konsentrat dengan formula  dashyat  3D Clean Action sehingga pakaian Anda  putih cemerlang, anti kuman dan bebas noda, ekstra detergency secara menyeluruh sampai ke serat kain.<br><br>Pakaian putih cemerlang  karena zat pencemerlang yang terkandung membuat pakaian putih seperti baru.<br>Anti Kuman mencegah  bau apek meskipun pengeringan tidak sempurna  atau direndam semalaman.<br>Bebas Noda dengan Formula Max Power mengangkat noda dengan tuntas. <br><i>* Takaran mencuci 1 sendok untuk 15 potong pakaian</i><br>', '/img/produk/small/produk-03.png', '/img/produk/medium/produk-3.png', '/img/produk/icon/handmachinewash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(4, 'Attack Batik Cleaner<br/>270 ml', 'Sabun Cair yang diciptakan khusus untuk mencuci batik dan bahan halus lainnya. Kandungan pembersihnya terbuat dari bahan alami yang berasal dari tumbuhan, sehingga baik untuk mencuci batik karena halus dan lembut. Sabun ini membuat warna batik tidak mudah pudar atau luntur dan wanginya segar alami.', '/img/produk/small/produk-04.png', '/img/produk/medium/produk-04.png', '/img/produk/icon/handwash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(5, 'Attack Auto Double Clean<br/>1000 ml (Bottle), 800 ml (Pouch)', 'Deterjen cair konsentrat untuk segala jenis mesin cuci, larut sempurna hingga ke serat kain, menghilangkan kotoran, tanpa sisa bubuk deterjen. Attack Auto SmoothCare membuat bentuk baju tetap terjaga dan tidak mudah kusut.Deterjen cair konsentrat untuk segala jenis mesin cuci, larut sempurna hingga ke serat kain tanpa sisa bubuk deterjen. Attack Auto Double Clean mengangkat noda serta mencegah timbulnya bakteri dan bau apek.', '/img/produk/small/produk-05.png', '/img/produk/medium/produk-05.png', '/img/produk/icon/machinewash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(6, 'Attack Auto Smoothcare<br/>1000 ml (Bottle), 800 ml (Pouch)', 'Deterjen cair konsentrat untuk segala jenis mesin cuci, larut sempurna hingga ke serat kain, menghilangkan kotoran, tanpa sisa bubuk deterjen. Attack Auto SmoothCare membuat bentuk baju tetap terjaga dan tidak mudah kusut.', '/img/produk/small/produk-06.png', '/img/produk/medium/produk-06.png', '/img/produk/icon/machinewash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(7, 'Attack Easy Purple Blossom<br/>48 gr, 700 gr, 1200 gr', 'Deterjen yang membuat mencuci menjadi ringan, serasa dibantu oleh Kekuatan 10 Tangan. Kini dengan formula baru Mengucek 2x lebih cepat efektif menghilangkan noda membandel sekalipun. Wangi Purple Blossom memberikan wangi semerbak bunga yang bermekaran', '/img/produk/small/produk-07.png', '/img/produk/medium/produk-07.png', '/img/produk/icon/handwash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(8, 'Attack Easy Romantic Flowers<br/>48 gr, 700 gr, 1200 gr', 'Deterjen yang membuat mencuci menjadi ringan, serasa dibantu oleh Kekuatan 10 Tangan. Kini dengan formula baru Mengucek 2x lebih cepat efektif menghilangkan noda membandel sekalipun. Wangi Romantic Flowers membuat pakaian bersih dan wangi tahan lama.', '/img/produk/small/produk-08.png', '/img/produk/medium/produk-08.png', '/img/produk/icon/handwash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(9, 'Attack Jaz1 Segar<br/>900 gr, 60 gr', 'Deterjen dengan "Jurus Perontok Super" yang merontokan kotoran sejak pakaian direndam.\r\n\r\n1 Solusi Tanpa Sabun Colek . Mantafff! Baju Bersih Wangi dengan wangi kesegaran bunga cukup dengan 5 menit perendaman tanpa perlu tambahan sabun colek', '/img/produk/small/produk-09.png', '/img/produk/medium/produk-09.png', '/img/produk/icon/handwash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(10, 'Attack Jaz1 Cinta<br/>900 gr, 60 gr', 'Deterjen dengan "Jurus Perontok Super" yang merontokan kotoran sejak pakaian direndam.\r\n\r\n1 Solusi Tanpa Sabun Colek . Mantafff! Baju Bersih Wangi dengan wangi semerbak bunga cukup dengan 5 menit perendaman tanpa perlu tambahan sabun colek', '/img/produk/small/produk-10.png', '/img/produk/medium/produk-10.png', '/img/produk/icon/handwash.png', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '');
